SELECT t.id AS id, t.todolist, t.isChecked, t.createdAt AS toDoCreatedAt, c.id AS categoryId, c.name AS categoryName, c.color AS categoryColor, s.id AS subcategoryId, s.name AS subcategoryName FROM to_do_list t LEFT JOIN subcategory s ON t.subcategoryId = s.id LEFT JOIN category c ON t.categoryId = c.id WHERE t.isChecked = false OR (t.createdAt >= #{start} AND t.createdAt < #{end})
AND c.id IN
#{id}
